
INSERT INTO articles(titre, descr, contenu, date_pub, date_modif, longitude, latitude)
VALUES ('Palais Présidentiel', '', 'Offrant une architecture impressionnante du XIXe siècle, le Palais Présidentiel est un symbole de l\'histoire malgache. Explorez ces magnifiques jardins et promenez-vous à travers les salles où de nombreux événements politiques importants ont eu lieu.', datetime('now'), datetime('now'), 47.5167, -18.9333);

INSERT INTO articles(titre, descr, contenu, date_pub, date_modif, longitude, latitude)
VALUES ('Parc de Tsarasaotra', '', 'Le Parc de Tsarasaotra abrite le lac Alarobia, un sanctuaire naturel au cœur de la zone industrielle. Découvrez une incroyable diversité d\'oiseaux endémiques et profitez d\'une atmosphère paisible et relaxante en explorant les sentiers et les mangroves.', datetime('now'), datetime('now'), 47.5167, -18.9167);

INSERT INTO articles(titre, descr, contenu, date_pub, date_modif, longitude, latitude)
VALUES ('Musée d\'Art et d\'Archéologie', '', 'Plongez dans l\'art et l\'archéologie de Madagascar en visitant ce musée situé à Isoraka. Admirez des collections d\'objets archéologiques et de décorations funéraires, offrant un aperçu captivant de l\'histoire et de la culture de l\'île.', datetime('now'), datetime('now'), 47.5333, -18.9167);

INSERT INTO articles(titre, descr, contenu, date_pub, date_modif, longitude, latitude)
VALUES ('Monument aux Morts', '', 'Érigé en souvenir des héros de la Première Guerre mondiale, le Monument aux Morts est une statue imposante d\'un ange doré. Promenez-vous sur l\'île reliée par une chaussée et prenez le temps de réfléchir à l\'importance de l\'histoire et du sacrifice.', datetime('now'), datetime('now'), 47.5079, -18.8792);

INSERT INTO articles(titre, descr, contenu, date_pub, date_modif, longitude, latitude)
VALUES ('Rova', '', 'Le Rova, bien que gravement endommagé par un incendie, reste un site emblématique d\'Antananarivo. Profitez de la vue panoramique sur la ville depuis cette ancienne résidence royale et imprégnez-vous de l\'atmosphère historique unique qui règne encore.', datetime('now'), datetime('now'), 47.5367, -18.9135);

INSERT INTO articles(titre, descr, contenu, date_pub, date_modif, longitude, latitude)
VALUES ('Croc\'Farm', '', 'Promenez-vous dans les magnifiques jardins de Croc\'Farm, abritant plus de 80 espèces animales, y compris des crocodiles, des tortues et des lémuriens. Découvrez la diversité impressionnante de la faune malgache lors de cette expérience immersive.', datetime('now'), datetime('now'), 47.5167, -18.8833);

INSERT INTO articles(titre, descr, contenu, date_pub, date_modif, longitude, latitude)
VALUES ('Parc Lemurs', '', 'Situé dans une zone de conservation, le Parc Lemurs est un sanctuaire pour les adorables lémuriens sauvages et secourus. Profitez d\'une expérience unique en observant ces animaux dans leur environnement naturel et en contribuant à leur préservation.', datetime('now'), datetime('now'), 47.5500, -18.9167);

INSERT INTO articles(titre, descr, contenu, date_pub, date_modif, longitude, latitude)
VALUES ('Chez Sucett\'s', '', 'Ce restaurant propose des plats malgaches et créoles (un mélange de cuisines africaine, asiatique et européenne). Vous pourriez y trouver des plats traditionnels tels que le Romazava (un ragoût de bœuf malgache) ou le Vary Amin\'anana (riz avec des feuilles vertes).', datetime('now'), datetime('now'), 47.5360, -18.9070);

INSERT INTO articles(titre, descr, contenu, date_pub, date_modif, longitude, latitude)
VALUES ('Saka Express', '', 'Un endroit idéal pour un déjeuner rapide, vous y trouverez des pizzas, des kebabs et des sandwiches avec une touche malgache.', datetime('now'), datetime('now'), 47.5360, -18.9070);

INSERT INTO articles(titre, descr, contenu, date_pub, date_modif, longitude, latitude)
VALUES ('Bread Mafan\'', '', 'Mix de pâtisserie et de café, ici vous pouvez vous attendre à une variété de pains, de pâtisseries et de desserts.', datetime('now'), datetime('now'), 47.5360, -18.9070);
